import React, { useState, useEffect } from 'react';
import { 
  Calendar, 
  DollarSign, 
  Users, 
  TrendingUp, 
  Plus, 
  Target,
  CheckCircle,
  Clock,
  Award,
  PieChart,
  TrendingDown,
  Receipt,
  X
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { useAppStore, useAuth, useCampaigns, useEvents, useSupporters, useUI } from '../../store/app_store';
import { apiService } from '../../services/apiService';
import EventCard from '../cards/EventCard';
import CampaignCard from '../cards/CampaignCard'; // NEW IMPORT
import CreateEventForm from '../events/CreateEventForm';
import CreateCampaignForm from '../campaigns/CreateCampaignForm'; // NEW IMPORT
import SupporterCard from '../supporters/cards/SupporterCard';
import CreateSupporterForm from '../supporters/forms/CreateSupporterForm';
import SupporterDetailPanel from '../supporters/panels/SupporterDetailPanel';
import { supporterService } from '../../services';
import MetricCard from './/MetricCard';
import DashboardHeader from './DashboardHeader';
import DashboardTabs from './DashboardTabs';
import OverviewTab from './OverviewTab';
import CampaignsTab from './CampaignsTab';

import { Event, Campaign, DashboardMetrics, CreateEventForm as CreateEventFormData, CreateCampaignForm as CreateCampaignFormData } from '../../types/types';

// Component-specific prop interfaces
interface MetricCardProps {
  icon: React.ComponentType<React.SVGProps<SVGSVGElement>>;
  title: string;
  value: string | number;
  subtitle?: string;
  color?: string;
}

export default function ClubDashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [financials, setFinancials] = useState<any>(null);
  const [loadingFinancials, setLoadingFinancials] = useState(false);

  // Enhanced state for event editing
  const [showCreateEventForm, setShowCreateEventForm] = useState(false);
  const [showEditEventForm, setShowEditEventForm] = useState(false);
  const [eventToEdit, setEventToEdit] = useState<Event | null>(null);

  // NEW: Enhanced state for campaign editing
  const [showCreateCampaignForm, setShowCreateCampaignForm] = useState(false);
  const [showEditCampaignForm, setShowEditCampaignForm] = useState(false);
  const [campaignToEdit, setCampaignToEdit] = useState<Campaign | null>(null);

  const navigate = useNavigate();

  // Use your existing app store hooks
  const { user, club } = useAuth();
  const { campaigns, addCampaign, updateCampaign, deleteCampaign } = useCampaigns();
  const { events, addEvent, updateEvent, deleteEvent } = useEvents();
  const { supporters } = useSupporters();
  const { isLoading, error } = useUI();
  const { loadClubData } = useAppStore();

  // Enhanced state for supporter management
const [showCreateSupporterForm, setShowCreateSupporterForm] = useState(false);
const [showEditSupporterForm, setShowEditSupporterForm] = useState(false);
const [supporterToEdit, setSupporterToEdit] = useState<any>(null);
const [showSupporterDetailPanel, setShowSupporterDetailPanel] = useState(false);
const [selectedSupporter, setSelectedSupporter] = useState<any>(null);

  // Load financial data
  const loadFinancialData = async () => {
    if (club?.id) {
      setLoadingFinancials(true);
      try {
        const financialData = await apiService.getClubFinancials(club.id);
        setFinancials(financialData);
      } catch (error) {
        console.error('Failed to load financial data:', error);
      } finally {
        setLoadingFinancials(false);
      }
    }
  };

  // Load data when component mounts
  useEffect(() => {
    if (club?.id) {
      console.log('Loading dashboard data for club:', club.id);
      loadClubData(); // Loads campaigns, events, supporters from your app store
      loadFinancialData(); // Loads financial data
    }
  }, [club?.id, loadClubData]);

  // Create computed metrics from real data
  const metrics: DashboardMetrics = {
    total_raised: financials?.total_income || 0,
    active_campaigns: campaigns.length,
    upcoming_events: events.filter(e => new Date(e.event_date) > new Date()).length,
    total_supporters: supporters.length,
    top_donors: supporters.filter(s => s.type === 'donor').slice(0, 3)
  };

  // Filter upcoming events from real data
  const upcomingEvents = events
    .filter(event => new Date(event.event_date) > new Date())
    .slice(0, 2); // Show only next 2 events

  // Get financial data from backend or use defaults
  const financialData = {
