sourcesContent:
import React, { useState, useEffect } from 'react';
import { 
  Calendar, 
  DollarSign, 
  Users, 
  TrendingUp, 
  Plus, 
  Target,
  CheckCircle,
  Clock,
  Award,
  PieChart,
  TrendingDown,
  Receipt,
  X
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { useAppStore, useAuth, useCampaigns, useEvents, useSupporters, useUI } from '../../store/app_store';
import { apiService } from '../../services/apiService';
import EventCard from '../cards/EventCard';
import CampaignCard from '../cards/CampaignCard'; // NEW IMPORT
import CreateEventForm from '../events/CreateEventForm';
import CreateCampaignForm from '../campaigns/CreateCampaignForm'; // NEW IMPORT
import SupporterCard from '../supporters/cards/SupporterCard';
import CreateSupporterForm from '../supporters/forms/CreateSupporterForm';
import SupporterDetailPanel from '../supporters/panels/SupporterDetailPanel';
import { supporterService } from '../../services';

import { Event, Campaign, DashboardMetrics, CreateEventForm as CreateEventFormData, CreateCampaignForm as CreateCampaignFormData } from '../../types/types';

// Component-specific prop interfaces
interface MetricCardProps {
  icon: React.ComponentType<React.SVGProps<SVGSVGElement>>;
  title: string;
  value: string | number;
  subtitle?: string;
  color?: string;
}

export default function ClubDashboard() {
  const [activeTab, setActiveTab] = useState('overview');
  const [financials, setFinancials] = useState<any>(null);
  const [loadingFinancials, setLoadingFinancials] = useState(false);

  // Enhanced state for event editing
  const [showCreateEventForm, setShowCreateEventForm] = useState(false);
  const [showEditEventForm, setShowEditEventForm] = useState(false);
  const [eventToEdit, setEventToEdit] = useState<Event | null>(null);

  // NEW: Enhanced state for campaign editing
  const [showCreateCampaignForm, setShowCreateCampaignForm] = useState(false);
  const [showEditCampaignForm, setShowEditCampaignForm] = useState(false);
  const [campaignToEdit, setCampaignToEdit] = useState<Campaign | null>(null);

  const navigate = useNavigate();

  // Use your existing app store hooks
  const { user, club } = useAuth();
  const { campaigns, addCampaign, updateCampaign, deleteCampaign } = useCampaigns();
  const { events, addEvent, updateEvent, deleteEvent } = useEvents();
  const { supporters } = useSupporters();
  const { isLoading, error } = useUI();
  const { loadClubData } = useAppStore();

  // Enhanced state for supporter management
const [showCreateSupporterForm, setShowCreateSupporterForm] = useState(false);
const [showEditSupporterForm, setShowEditSupporterForm] = useState(false);
const [supporterToEdit, setSupporterToEdit] = useState<any>(null);
const [showSupporterDetailPanel, setShowSupporterDetailPanel] = useState(false);
const [selectedSupporter, setSelectedSupporter] = useState<any>(null);

  // Load financial data
  const loadFinancialData = async () => {
    if (club?.id) {
      setLoadingFinancials(true);
      try {
        const financialData = await apiService.getClubFinancials(club.id);
        setFinancials(financialData);
      } catch (error) {
        console.error('Failed to load financial data:', error);
      } finally {
        setLoadingFinancials(false);
      }
    }
  };

  // Load data when component mounts
  useEffect(() => {
    if (club?.id) {
      console.log('Loading dashboard data for club:', club.id);
      loadClubData(); // Loads campaigns, events, supporters from your app store
      loadFinancialData(); // Loads financial data
    }
  }, [club?.id, loadClubData]);

  // Create computed metrics from real data
  const metrics: DashboardMetrics = {
    total_raised: financials?.total_income || 0,
    active_campaigns: campaigns.length,
    upcoming_events: events.filter(e => new Date(e.event_date) > new Date()).length,
    total_supporters: supporters.length,
    top_donors: supporters.filter(s => s.type === 'donor').slice(0, 3)
  };

  // Filter upcoming events from real data
  const upcomingEvents = events
    .filter(event => new Date(event.event_date) > new Date())
    .slice(0, 2); // Show only next 2 events

  // Get financial data from backend or use defaults
  const financialData = {
    monthly_income: financials?.total_income || 0,
    monthly_expenses: financials?.total_expenses || 0,
    net_profit: financials?.net_profit || 0,
    pending_expenses: 0, // You might need to add this to your backend
    recent_transactions: [], // You might need to add this to your backend
    expense_categories: financials?.expense_breakdown || []
  };

  // Event handlers for the EventCard
  const handleEditEvent = (event: Event) => {
    console.log('ðŸ–Šï¸ Opening edit form for event:', event.title);
    setEventToEdit(event);
    setShowEditEventForm(true);
  };

  const handleDeleteEvent = async (eventId: string) => {
    const event = events.find(e => e.id === eventId);
    if (!event) return;

    const confirmMessage = `Are you sure you want to delete "${event.title}"? This action cannot be undone.`;

    if (window.confirm(confirmMessage)) {
      try {
        await apiService.deleteEvent(eventId);
        deleteEvent(eventId); // Remove from store

        // Refresh financial data since event was deleted
        if (club?.id) {
          loadFinancialData();
        }

        console.log('âœ… Event deleted successfully');
      } catch (error) {
        console.error('âŒ Failed to delete event:', error);
        alert('Failed to delete event. Please try again.');
      }
    }
  };

  const handleViewEvent = (event: Event) => {
    console.log('ðŸ‘ï¸ View event:', event.title);
    // TODO: Navigate to event details page or show event details modal
    // navigate(`/events/${event.id}`);
  };

  // NEW: Campaign handlers for the CampaignCard
  const handleEditCampaign = (campaign: Campaign) => {
    console.log('ðŸ–Šï¸ Opening edit form for campaign:', campaign.name);
    setCampaignToEdit(campaign);
    setShowEditCampaignForm(true);
  };

  const handleDeleteCampaign = async (campaignId: string) => {
    const campaign = campaigns.find(c => c.id === campaignId);
    if (!campaign) return;

    const confirmMessage = `Are you sure you want to delete "${campaign.name}"? This action cannot be undone.`;

    if (window.confirm(confirmMessage)) {
      try {
        await apiService.deleteCampaign(campaignId);
        deleteCampaign(campaignId); // Remove from store

        // Refresh financial data since campaign was deleted
        if (club?.id) {
          loadFinancialData();
        }

        console.log('âœ… Campaign deleted successfully');
      } catch (error) {
        console.error('âŒ Failed to delete campaign:', error);
        alert('Failed to delete campaign. Please try again.');
      }
    }
  };

  const handleViewCampaign = (campaign: Campaign) => {
    console.log('ðŸ‘ï¸ View campaign:', campaign.name);
    // TODO: Navigate to campaign details page or show campaign details modal
    // navigate(`/campaigns/${campaign.id}`);
  };

  // Create event handler
  const handleCreateEvent = async (eventData: CreateEventFormData) => {
    try {
      console.log('ðŸ“¤ Creating new event:', eventData);
      const response = await apiService.createEvent(eventData);
      addEvent(response.event); // Add to store

      // Refresh financial data
      if (club?.id) {
        loadFinancialData();
      }

      return response; // Return for success handling
    } catch (error) {
      console.error('âŒ Failed to create event:', error);
      throw error; // Re-throw for form error handling
    }
  };

  // Edit event handler
  const handleUpdateEvent = async (eventData: CreateEventFormData) => {
    if (!eventToEdit) return;

    try {
      console.log('ðŸ“¤ Updating event:', eventData);
      const response = await apiService.updateEvent(eventToEdit.id, eventData);
      updateEvent(eventToEdit.id, response.event); // Update in store

      // Refresh financial data
      if (club?.id) {
        loadFinancialData();
      }

      return response; // Return for success handling
    } catch (error) {
      console.error('âŒ Failed to update event:', error);
      throw error; // Re-throw for form error handling
    }
  };

  // NEW: Create campaign handler
  const handleCreateCampaign = async (campaignData: CreateCampaignFormData) => {
    try {
      console.log('ðŸ“¤ Creating new campaign:', campaignData);
      const response = await apiService.createCampaign(campaignData);
      addCampaign(response.campaign); // Add to store

      // Refresh financial data
      if (club?.id) {
        loadFinancialData();
      }

      return response; // Return for success handling
    } catch (error) {
      console.error('âŒ Failed to create campaign:', error);
      throw error; // Re-throw for form error handling
    }
  };

  // NEW: Edit campaign handler
  const handleUpdateCampaign = async (campaignData: CreateCampaignFormData) => {
    if (!campaignToEdit) return;

    try {
      console.log('ðŸ“¤ Updating campaign:', campaignData);
      const response = await apiService.updateCampaign(campaignToEdit.id, campaignData);
      updateCampaign(campaignToEdit.id, response.campaign); // Update in store

      // Refresh financial data
      if (club?.id) {
        loadFinancialData();
      }

      return response; // Return for success handling
    } catch (error) {
      console.error('âŒ Failed to update campaign:', error);
      throw error; // Re-throw for form error handling
    }
  };

  // Get campaign name helper
  const getCampaignName = (campaignId?: string) => {
    if (!campaignId) return undefined;
    const campaign = campaigns.find(c => c.id === campaignId);
    return campaign?.name;
  };

  // UPDATED: Now opens the create campaign form instead of navigating
  const handleOpenCreateCampaign = () => {
    console.log('ðŸ“ Opening create campaign form');
    setShowCreateCampaignForm(true);
  };

  // Keep your existing component definitions
  const MetricCard: React.FC<MetricCardProps> = ({ icon: Icon, title, value, subtitle, color = 'blue' }) => (
    <div className="bg-white rounded-lg border border-gray-200 p-6 shadow-sm">
      <div className="flex items-center">
        <div className={`flex-shrink-0 p-3 rounded-lg bg-${color}-100`}>
          <Icon className={`h-6 w-6 text-${color}-600`} />
        </div>
        <div className="ml-4">
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900">{value}</p>
          {subtitle && <p className="text-sm text-gray-500">{subtitle}</p>}
        </div>
      </div>
    </div>
  );

 // NEW: Supporter handlers for the SupporterCard
const handleEditSupporter = (supporter: any) => {
  console.log('ðŸ–Šï¸ Opening edit form for supporter:', supporter.name);
  setSupporterToEdit(supporter);
  setShowEditSupporterForm(true);
};

const handleDeleteSupporter = async (supporterId: string) => {
  const supporter = supporters.find(s => s.id === supporterId);
  if (!supporter) return;

  const confirmMessage = `Are you sure you want to delete "${supporter.name}"? This action cannot be undone.`;

  if (window.confirm(confirmMessage)) {
    try {
      await supporterService.deleteSupporter(supporterId);
      // Remove from store (you'll need to add this to your store)
      // deleteSupporter(supporterId);

      console.log('âœ… Supporter deleted successfully');

      // Refresh supporter data
      if (club?.id) {
        loadClubData();
      }
    } catch (error) {
      console.error('âŒ Failed to delete supporter:', error);
      alert('Failed to delete supporter. Please try again.');
    }
  }
};

const handleViewSupporter = (supporter: any) => {
  console.log('ðŸ‘ï¸ View supporter:', supporter.name);
  setSelectedSupporter(supporter);
  setShowSupporterDetailPanel(true);
};

const handleQuickCall = (supporter: any) => {
  console.log('ðŸ“ž Quick call to:', supporter.name);
  // TODO: Implement quick call functionality
  // Could open a communication logging modal
  alert(`Calling ${supporter.name} at ${supporter.phone}`);
};

const handleQuickEmail = (supporter: any) => {
  console.log('ðŸ“§ Quick email to:', supporter.name);
  // TODO: Implement quick email functionality
  // Could open email client or communication logging modal
  window.open(`mailto:${supporter.email}?subject=Hello from ${club?.name}`);
};

// Create supporter handler
const handleCreateSupporter = async (supporterData: any) => {
  try {
    console.log('ðŸ“¤ Creating new supporter:', supporterData);
    const response = await supporterService.createSupporter(club!.id, supporterData);

    // Add to store (you'll need to add this to your store)
    // addSupporter(response.supporter);

    // Refresh supporter data
    if (club?.id) {
      loadClubData();
    }

    return response; // Return for success handling
  } catch (error) {
    console.error('âŒ Failed to create supporter:', error);
    throw error; // Re-throw for form error handling
  }
};

// Edit supporter handler
const handleUpdateSupporter = async (supporterData: any) => {
  if (!supporterToEdit) return;

  try {
    console.log('ðŸ“¤ Updating supporter:', supporterData);
    const response = await supporterService.updateSupporter(supporterToEdit.id, supporterData);

    // Update in store (you'll need to add this to your store)
    // updateSupporter(supporterToEdit.id, response.supporter);

    // Refresh supporter data
    if (club?.id) {
      loadClubData();
    }

    return response; // Return for success handling
  } catch (error) {
    console.error('âŒ Failed to update supporter:', error);
    throw error; // Re-throw for form error handling
  }
};

  // Show loading state while data is being fetched
  if (isLoading && (!campaigns.length && !events.length)) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading your dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">
                {club?.name || 'FundRaisely Club'}
              </h1>
              <p className="text-gray-600">Welcome back, {user?.name}!</p>
            </div>
            <div className="flex space-x-3">
              <button 
                onClick={() => setShowCreateEventForm(true)}
                className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center"
              >
                <Plus className="h-4 w-4 mr-2" />
                New Event
              </button>
              <button 
                onClick={handleOpenCreateCampaign}
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg flex items-center"
              >
                <Target className="h-4 w-4 mr-2" />
                New Campaign
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {['overview', 'campaigns', 'events', 'supporters', 'financials'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`py-4 px-1 border-b-2 font-medium text-sm capitalize ${
                  activeTab === tab
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 pt-4">
          <div className="bg-red-50 border border-red-200 text-red-600 px-4 py-3 rounded-lg">
            {error}
          </div>
        </div>
      )}

      {/* Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {activeTab === 'overview' && (
          <div className="space-y-8">
            {/* Metrics using real data */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-6">
              <MetricCard
                icon={DollarSign}
                title="Total Raised"
                value={`Â£${metrics.total_raised.toLocaleString()}`}
                subtitle="This year"
                color="green"
              />
              <MetricCard
                icon={TrendingDown}
                title="Total Expenses"
                value={`Â£${financialData.monthly_expenses.toLocaleString()}`}
                subtitle="This year"
                color="red"
              />
              <MetricCard
                icon={TrendingUp}
                title="Net Profit"
                value={`Â£${financialData.net_profit.toLocaleString()}`}
                subtitle="This year"
                color="blue"
              />
              <MetricCard
                icon={Target}
                title="Active Campaigns"
                value={metrics.active_campaigns}
                subtitle="In progress"
                color="purple"
              />
              <MetricCard
                icon={Calendar}
                title="Upcoming Events"
                value={metrics.upcoming_events}
                subtitle="Next 30 days"
                color="orange"
              />
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
              <div className="lg:col-span-2">
                <div className="bg-white rounded-lg border border-gray-200 shadow-sm">
                  <div className="px-6 py-4 border-b border-gray-200">
                    <h2 className="text-lg font-semibold text-gray-900">Upcoming Events</h2>
                  </div>
                  <div className="p-6 space-y-4">
                    {upcomingEvents.length > 0 ? (
                      upcomingEvents.map((event) => (
                        <EventCard
                          key={event.id}
                          event={event}
                          onEdit={handleEditEvent}
