import React, { useState, useEffect } from 'react';
import { Calendar, DollarSign, Tag, Users, Save, X, MapPin, FileText } from 'lucide-react';
import { CreateEventForm as CreateEventFormData, Campaign } from '../../types/types';
import { useAuth, useUI, useCampaigns } from '../../store/app_store';
import { apiService } from '../../services/apiService';

interface CreateEventFormProps {
  onSubmit?: (eventData: CreateEventFormData) => void; // Keep for backward compatibility
  onSuccess?: (event: any) => void; // New callback for successful creation
  onCancel: () => void;
  campaigns?: Campaign[];
}

interface FormErrors {
  [key: string]: string;
}

const CreateEventForm: React.FC<CreateEventFormProps> = ({ 
  onSubmit, // Legacy prop
  onSuccess, // New prop
  onCancel, 
  campaigns = [] 
}) => {
  const { club } = useAuth();
  const { isLoading, setLoading, setError, clearError } = useUI();
  const { campaigns: storeCampaigns } = useCampaigns();

  // Use campaigns from props or store
  const availableCampaigns = campaigns.length > 0 ? campaigns : storeCampaigns;

  const [formData, setFormData] = useState<CreateEventFormData>({
    title: '',
    type: '',
    description: '',
    venue: '',
    max_participants: undefined,
    goal_amount: 0,
    event_date: '',
    campaign_id: ''
  });

  const [errors, setErrors] = useState<FormErrors>({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Load campaigns when component mounts
  useEffect(() => {
    const loadCampaigns = async () => {
      if (club?.id && availableCampaigns.length === 0) {
        try {
          const response = await apiService.getClubCampaigns(club.id);
          // FIXED: Access response.campaigns directly (not response.data)
          console.log('üìã Available campaigns:', response.campaigns || []);
        } catch (error) {
          console.error('‚ùå Failed to load campaigns:', error);
        }
      }
    };
    loadCampaigns();
  }, [club?.id, availableCampaigns.length]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;

    // Handle different input types properly
    let processedValue: any = value;

    if (name === 'goal_amount') {
      processedValue = value ? parseFloat(value) : 0;
    } else if (name === 'max_participants') {
      processedValue = value ? parseInt(value) : undefined;
    }

    setFormData(prev => ({
      ...prev,
      [name]: processedValue
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    // Clear global error
    clearError();
  };

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.title.trim()) {
      newErrors.title = 'Event title is required';
    }

    if (!formData.type.trim()) {
      newErrors.type = 'Event type is required';
    }

    if (!formData.event_date) {
      newErrors.event_date = 'Event date is required';
    } else {
      const eventDate = new Date(formData.event_date);
      const today = new Date();
      today.setHours(0, 0, 0, 0);
      if (eventDate < today) {
        newErrors.event_date = 'Event date cannot be in the past';
      }
    }

    if (!formData.goal_amount || formData.goal_amount <= 0) {
      newErrors.goal_amount = 'Goal amount must be greater than 0';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }

    // If legacy onSubmit prop is provided, use it (for backward compatibility)
    if (onSubmit) {
      console.log('üì§ Using legacy onSubmit handler');
      onSubmit(formData);
      return;
    }

    // Otherwise, call API directly
    if (!club?.id) {
      setError('No club found. Please log in again.');
      return;
    }

    setIsSubmitting(true);

    try {
      clearError();

      // Prepare data for API (match your backend expectations exactly)
      const eventData = {
        title: formData.title.trim(),
        type: formData.type.trim(),
        description: formData.description?.trim() || undefined,
        venue: formData.venue?.trim() || undefined,
        max_participants: formData.max_participants || undefined,
        goal_amount: formData.goal_amount,
        event_date: formData.event_date,
        campaign_id: formData.campaign_id || undefined
      };

      console.log('üöÄ Creating event with data:', eventData);
      console.log('üîë Club ID:', club.id);
      console.log('üé´ Auth Token:', localStorage.getItem('auth_token') ? 'Present' : 'Missing');

      // Call API to create event
      const response = await apiService.createEvent(eventData);

      console.log('‚úÖ Event created successfully:', response);

      // FIXED: Call success callback with the event object from response.event
      if (onSuccess) {
        onSuccess(response.event || response);
      }

      // Close the form
      onCancel();

      // Show success message
      alert('Event created successfully!');

    } catch (error) {
      console.error('‚ùå Failed to create event:', error);
      const errorMessage = error instanceof Error ? error.message : 'Failed to create event';
      setError(errorMessage);

      // Also show alert for immediate feedback
      alert(`Error creating event: ${errorMessage}`);
    } finally {
      setIsSubmitting(false);
    }
  };

  // FIXED: Match backend validation - use lowercase values that map to backend
  const eventTypeSuggestions = [
    { label: 'Quiz Night', value: 'quiz' },
    { label: 'Bingo', value: 'bingo' },
    { label: 'Raffle', value: 'raffle' },
    { label: 'Auction', value: 'auction' },
    { label: 'Dinner/Social', value: 'dinner' },
    { label: 'Concert', value: 'concert' },
    { label: 'Sports Event', value: 'sports' },
    { label: 'Other', value: 'other' }
  ];

  return (
    <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900">Create New Event</h2>
            <button
              onClick={onCancel}
              className="text-gray-400 hover:text-gray-600"
              disabled={isSubmitting}
            >
              <X className="h-6 w-6" />
            </button>
          </div>
        </div>

        {/* Form */}
        <div className="px-6 py-4 space-y-6">
          {/* Debug Info */}
          {process.env.NODE_ENV === 'development' && (
            <div className="bg-blue-50 border border-blue-200 rounded p-3 text-xs">
              <strong>Debug Info:</strong> Club ID: {club?.id || 'None'} | 
              Auth Token: {localStorage.getItem('auth_token') ? '‚úÖ' : '‚ùå'} |
              Available Campaigns: {availableCampaigns.length}
            </div>
          )}

          {/* Event Title */}
          <div>
            <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
              Event Title *
            </label>
            <div className="relative">
              <FileText className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                disabled={isSubmitting}
                className={`w-full pl-10 pr-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                  errors.title ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-blue-500'
                } ${isSubmitting ? 'bg-gray-50' : ''}`}
                placeholder="e.g., Christmas Quiz Night"
              />
            </div>
            {errors.title && <p className="mt-1 text-sm text-red-600">{errors.title}</p>}
          </div>

          {/* Event Type */}
          <div>
            <label htmlFor="type" className="block text-sm font-medium text-gray-700 mb-1">
              Event Type *
            </label>
            <div className="space-y-3">
              {/* Popular event type suggestions */}
              <div className="flex flex-wrap gap-2 mb-3">
                {eventTypeSuggestions.map((suggestion) => (
                  <button
                    key={suggestion.value}
                    type="button"
                    disabled={isSubmitting}
                    onClick={() => setFormData(prev => ({ 
                      ...prev, 
                      type: suggestion.value 
                    }))}
                    className={`px-3 py-1 text-sm rounded-full border transition-colors ${
                      formData.type === suggestion.value
                        ? 'bg-blue-100 border-blue-300 text-blue-700'
                        : 'bg-gray-100 border-gray-300 text-gray-700 hover:bg-gray-200'
                    } ${isSubmitting ? 'opacity-50 cursor-not-allowed' : ''}`}
                  >
                    {suggestion.label}
                  </button>
                ))}
              </div>

              {/* Custom type input */}
              <div className="relative">
                <Tag className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  id="type"
                  name="type"
                  value={formData.type}
                  onChange={handleInputChange}
                  disabled={isSubmitting}
                  className={`w-full pl-10 pr-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.type ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-blue-500'
                  } ${isSubmitting ? 'bg-gray-50' : ''}`}
                  placeholder="Or enter your own event type..."
                />
              </div>
              {errors.type && <p className="mt-1 text-sm text-red-600">{errors.type}</p>}
              <p className="text-xs text-gray-500">
                Click a suggestion above or type: quiz, bingo, raffle, auction, dinner, concert, sports, or other
              </p>
            </div>
          </div>

          {/* Date and Goal Amount */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="event_date" className="block text-sm font-medium text-gray-700 mb-1">
                Event Date *
              </label>
              <div className="relative">
                <Calendar className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                <input
                  type="date"
                  id="event_date"
                  name="event_date"
                  value={formData.event_date}
                  onChange={handleInputChange}
                  disabled={isSubmitting}
                  className={`w-full pl-10 pr-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.event_date ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-blue-500'
                  } ${isSubmitting ? 'bg-gray-50' : ''}`}
                />
              </div>
              {errors.event_date && <p className="mt-1 text-sm text-red-600">{errors.event_date}</p>}
            </div>

            <div>
              <label htmlFor="goal_amount" className="block text-sm font-medium text-gray-700 mb-1">
                Fundraising Goal *
              </label>
              <div className="relative">
                <DollarSign className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                <input
                  type="number"
                  id="goal_amount"
                  name="goal_amount"
                  value={formData.goal_amount || ''}
                  onChange={handleInputChange}
                  disabled={isSubmitting}
                  className={`w-full pl-10 pr-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 ${
                    errors.goal_amount ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-blue-500'
                  } ${isSubmitting ? 'bg-gray-50' : ''}`}
                  placeholder="0.00"
                  min="0"
                  step="0.01"
                />
              </div>
              {errors.goal_amount && <p className="mt-1 text-sm text-red-600">{errors.goal_amount}</p>}
            </div>
          </div>

          {/* Campaign Assignment */}
          {availableCampaigns.length > 0 && (
            <div>
              <label htmlFor="campaign_id" className="block text-sm font-medium text-gray-700 mb-1">
                Assign to Campaign (Optional)
              </label>
              <div className="relative">
                <Tag className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                <select
                  id="campaign_id"
                  name="campaign_id"
                  value={formData.campaign_id || ''}
                  onChange={handleInputChange}
                  disabled={isSubmitting}
                  className={`w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                    isSubmitting ? 'bg-gray-50' : ''
                  }`}
                >
                  <option value="">Select a campaign (optional)</option>
                  {availableCampaigns.map((campaign) => (
                    <option key={campaign.id} value={campaign.id}>
                      {campaign.name}
                    </option>
                  ))}
                </select>
              </div>
            </div>
          )}

          {/* Venue and Max Participants */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label htmlFor="venue" className="block text-sm font-medium text-gray-700 mb-1">
                Venue
              </label>
              <div className="relative">
                <MapPin className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  id="venue"
                  name="venue"
                  value={formData.venue || ''}
                  onChange={handleInputChange}
                  disabled={isSubmitting}
                  className={`w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                    isSubmitting ? 'bg-gray-50' : ''
                  }`}
                  placeholder="e.g., Community Hall"
                />
              </div>
            </div>

            <div>
              <label htmlFor="max_participants" className="block text-sm font-medium text-gray-700 mb-1">
                Max Participants
              </label>
              <div className="relative">
                <Users className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
                <input
                  type="number"
                  id="max_participants"
                  name="max_participants"
                  value={formData.max_participants || ''}
                  onChange={handleInputChange}
                  disabled={isSubmitting}
                  className={`w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                    isSubmitting ? 'bg-gray-50' : ''
                  }`}
                  placeholder="Unlimited"
                  min="1"
                />
              </div>
            </div>
          </div>

          {/* Description */}
          <div>
            <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
              Description
            </label>
            <textarea
              id="description"
              name="description"
              value={formData.description || ''}
              onChange={handleInputChange}
              disabled={isSubmitting}
              rows={3}
              className={`w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                isSubmitting ? 'bg-gray-50' : ''
              }`}
              placeholder="Brief description of the event..."
            />
          </div>

          {/* Form Actions */}
          <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200">
            <button
              type="button"
              onClick={onCancel}
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
            >
              Cancel
            </button>
            <button
              type="button"
              onClick={handleSubmit}
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 flex items-center disabled:opacity-50"
            >
              <Save className="h-4 w-4 mr-2" />
              {isSubmitting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Creating...
                </>
              ) : (
                'Create Event'
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateEventForm;
