import React, { useState, useEffect } from 'react';
import {
  Calendar,
  DollarSign,
  Users,
  TrendingUp,
  Plus,
  Target,
  CheckCircle,
  Clock,
  Award,
  PieChart,
  TrendingDown,
  Receipt,
  X
} from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import {
  useAppStore,
  useAuth,
  useCampaigns,
  useEvents,
  useSupporters,
  useUI
} from '../../store/app_store';
import { apiService } from '../../services/apiService';
import { supporterService } from '../../services';

import EventCard from '../cards/EventCard';
import CampaignCard from '../cards/CampaignCard';
import SupporterCard from '../supporters/cards/SupporterCard';

import CreateEventForm from '../events/CreateEventForm';
import CreateCampaignForm from '../campaigns/CreateCampaignForm';
import CreateSupporterForm from '../supporters/forms/CreateSupporterForm';
import SupporterDetailPanel from '../supporters/panels/SupporterDetailPanel';

import {
  Event,
  Campaign,
  DashboardMetrics,
  CreateEventForm as CreateEventFormData,
  CreateCampaignForm as CreateCampaignFormData
} from '../../types/types';

interface MetricCardProps {
  icon: React.ComponentType<React.SVGProps<SVGSVGElement>>;
  title: string;
  value: string | number;
  subtitle?: string;
  color?: string;
}

const MetricCard: React.FC<MetricCardProps> = ({ icon: Icon, title, value, subtitle, color = 'blue' }) => (
  <div className="bg-white rounded-lg border border-gray-200 p-6 shadow-sm">
    <div className="flex items-center">
      <div className={`flex-shrink-0 p-3 rounded-lg bg-${color}-100`}>
        <Icon className={`h-6 w-6 text-${color}-600`} />
      </div>
      <div className="ml-4">
        <p className="text-sm font-medium text-gray-600">{title}</p>
        <p className="text-2xl font-bold text-gray-900">{value}</p>
        {subtitle && <p className="text-sm text-gray-500">{subtitle}</p>}
      </div>
    </div>
  </div>
);

export default function ClubDashboard() {
  const renderContent = () => {
    return (
      <div className="min-h-screen bg-gray-50">
        {/* ... all activeTab sections and modals go here */}
        {/* To keep this clean, break the JSX into individual components if needed */}
      </div>
    );
  }
  const [activeTab, setActiveTab] = useState('overview');
  const [financials, setFinancials] = useState<any>(null);
  const [loadingFinancials, setLoadingFinancials] = useState(false);

  const [showCreateEventForm, setShowCreateEventForm] = useState(false);
  const [showEditEventForm, setShowEditEventForm] = useState(false);
  const [eventToEdit, setEventToEdit] = useState<Event | null>(null);

  const [showCreateCampaignForm, setShowCreateCampaignForm] = useState(false);
  const [showEditCampaignForm, setShowEditCampaignForm] = useState(false);
  const [campaignToEdit, setCampaignToEdit] = useState<Campaign | null>(null);

  const [showCreateSupporterForm, setShowCreateSupporterForm] = useState(false);
  const [showEditSupporterForm, setShowEditSupporterForm] = useState(false);
  const [supporterToEdit, setSupporterToEdit] = useState<any>(null);
  const [showSupporterDetailPanel, setShowSupporterDetailPanel] = useState(false);
  const [selectedSupporter, setSelectedSupporter] = useState<any>(null);

  const navigate = useNavigate();

  const { user, club } = useAuth();
  const { campaigns, addCampaign, updateCampaign, deleteCampaign } = useCampaigns();
  const { events, addEvent, updateEvent, deleteEvent } = useEvents();
  const { supporters } = useSupporters();
  const { isLoading, error } = useUI();
  const { loadClubData } = useAppStore();

  const loadFinancialData = async () => {
    if (club?.id) {
      setLoadingFinancials(true);
      try {
        const financialData = await apiService.getClubFinancials(club.id);
        setFinancials(financialData);
      } catch (error) {
        console.error('Failed to load financial data:', error);
      } finally {
        setLoadingFinancials(false);
      }
    }
  };

  useEffect(() => {
    if (club?.id) {
      loadClubData();
      loadFinancialData();
    }
  }, [club?.id, loadClubData]);

  const handleEditSupporter = (supporter: any) => {
    setSupporterToEdit(supporter);
    setShowEditSupporterForm(true);
  };

  const handleDeleteSupporter = async (supporterId: string) => {
    const supporter = supporters.find(s => s.id === supporterId);
    if (!supporter) return;
    if (window.confirm(`Are you sure you want to delete "${supporter.name}"? This action cannot be undone.`)) {
      try {
        await supporterService.deleteSupporter(supporterId);
        if (club?.id) loadClubData();
      } catch (error) {
        console.error('Failed to delete supporter:', error);
        alert('Failed to delete supporter. Please try again.');
      }
    }
  };

  const handleViewSupporter = (supporter: any) => {
    setSelectedSupporter(supporter);
    setShowSupporterDetailPanel(true);
  };

  const handleQuickCall = (supporter: any) => {
    alert(`Calling ${supporter.name} at ${supporter.phone}`);
  };

  const handleQuickEmail = (supporter: any) => {
    window.open(`mailto:${supporter.email}?subject=Hello from ${club?.name}`);
  };

  const handleCreateSupporter = async (supporterData: any) => {
    try {
      const response = await supporterService.createSupporter(club!.id, supporterData);
      if (club?.id) loadClubData();
      return response;
    } catch (error) {
      console.error('Failed to create supporter:', error);
      throw error;
    }
  };

  const handleUpdateSupporter = async (supporterData: any) => {
    if (!supporterToEdit) return;
    try {
      const response = await supporterService.updateSupporter(supporterToEdit.id, supporterData);
      if (club?.id) loadClubData();
      return response;
    } catch (error) {
      console.error('Failed to update supporter:', error);
      throw error;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {activeTab === 'supporters' && (
  <div className="space-y-6">
    <div className="flex justify-between items-center">
      <h2 className="text-2xl font-bold text-gray-900">Supporters</h2>
      <button
        onClick={() => setShowCreateSupporterForm(true)}
        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center"
      >
        <Plus className="h-4 w-4 mr-2" />
        Add Supporter
      </button>
    </div>

    {supporters.length > 0 ? (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {supporters.map((supporter) => (
          <SupporterCard
            key={supporter.id}
            supporter={supporter}
            onEdit={handleEditSupporter}
            onDelete={handleDeleteSupporter}
            onView={handleViewSupporter}
            onQuickCall={handleQuickCall}
            onQuickEmail={handleQuickEmail}
          />
        ))}
      </div>
    ) : (
      <div className="text-center py-12">
        <Users className="mx-auto h-12 w-12 text-gray-400" />
        <h3 className="mt-2 text-sm font-medium text-gray-900">No supporters yet</h3>
        <p className="mt-1 text-sm text-gray-500">Add supporters to track volunteers, donors, and sponsors</p>
        <button
          onClick={() => setShowCreateSupporterForm(true)}
          className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
        >
          Add First Supporter
        </button>
      </div>
    )}
  </div>
)}

{/* Create Supporter Form Modal */}
{showCreateSupporterForm && (
  <CreateSupporterForm
    onSubmit={handleCreateSupporter}
    onCancel={() => setShowCreateSupporterForm(false)}
  />
)}

{/* Edit Supporter Form Modal */}
{showEditSupporterForm && supporterToEdit && (
  <CreateSupporterForm
    onSubmit={handleUpdateSupporter}
    onCancel={() => {
      setShowEditSupporterForm(false);
      setSupporterToEdit(null);
    }}
    editMode={true}
    existingSupporter={supporterToEdit}
  />
)}

{/* Supporter Detail Panel */}
{showSupporterDetailPanel && selectedSupporter && (
  <SupporterDetailPanel
    supporter={selectedSupporter}
    isOpen={showSupporterDetailPanel}
    onClose={() => {
      setShowSupporterDetailPanel(false);
      setSelectedSupporter(null);
    }}
  />
)}
  {activeTab === 'events' && (
  <div className="space-y-6">
    <div className="flex justify-between items-center">
      <h2 className="text-2xl font-bold text-gray-900">Events</h2>
      <button
        onClick={() => setShowCreateEventForm(true)}
        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg flex items-center"
      >
        <Plus className="h-4 w-4 mr-2" />
        New Event
      </button>
    </div>

    {events.length > 0 ? (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {events.map((event) => (
          <EventCard
            key={event.id}
            event={event}
            onEdit={() => {
              setEventToEdit(event);
              setShowEditEventForm(true);
            }}
            onDelete={async () => {
              const confirmMessage = `Are you sure you want to delete \"${event.title}\"? This action cannot be undone.`;
              if (window.confirm(confirmMessage)) {
                try {
                  await apiService.deleteEvent(event.id);
                  deleteEvent(event.id);
                  if (club?.id) loadFinancialData();
                } catch (error) {
                  console.error('Failed to delete event:', error);
                  alert('Failed to delete event. Please try again.');
                }
              }
            }}
            onView={() => navigate(`/events/${event.id}`)}
            campaignName={campaigns.find(c => c.id === event.campaign_id)?.name}
          />
        ))}
      </div>
    ) : (
      <div className="text-center py-12">
        <Calendar className="mx-auto h-12 w-12 text-gray-400" />
        <h3 className="mt-2 text-sm font-medium text-gray-900">No events yet</h3>
        <p className="mt-1 text-sm text-gray-500">Create your first event to get started</p>
        <button
          onClick={() => setShowCreateEventForm(true)}
          className="mt-4 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg"
        >
          Create Event
        </button>
      </div>
    )}
  </div>
)}

{/* Create Event Form Modal */}
{showCreateEventForm && (
  <CreateEventForm
    onSubmit={async (eventData) => {
      try {
        const response = await apiService.createEvent(eventData);
        addEvent(response.event);
        if (club?.id) loadFinancialData();
        setShowCreateEventForm(false);
      } catch (error) {
        console.error('Failed to create event:', error);
      }
    }}
    onCancel={() => setShowCreateEventForm(false)}
    campaigns={campaigns}
  />
)}

{/* Edit Event Form Modal */}
{showEditEventForm && eventToEdit && (
  <CreateEventForm
    onSubmit={async (eventData) => {
      try {
        const response = await apiService.updateEvent(eventToEdit.id, eventData);
        updateEvent(eventToEdit.id, response.event);
        if (club?.id) loadFinancialData();
        setShowEditEventForm(false);
        setEventToEdit(null);
      } catch (error) {
        console.error('Failed to update event:', error);
      }
    }}
    onCancel={() => {
      setShowEditEventForm(false);
      setEventToEdit(null);
    }}
    campaigns={campaigns}
    editMode={true}
    existingEvent={eventToEdit}
  />
)}

{activeTab === 'campaigns' && (
  <div className="space-y-6">
    <div className="flex justify-between items-center">
      <h2 className="text-2xl font-bold text-gray-900">Campaigns</h2>
      <button
        onClick={() => setShowCreateCampaignForm(true)}
        className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg flex items-center"
      >
        <Plus className="h-4 w-4 mr-2" />
        New Campaign
      </button>
    </div>

    {campaigns.length > 0 ? (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {campaigns.map((campaign) => (
          <CampaignCard
            key={campaign.id}
            campaign={campaign}
            onEdit={() => {
              setCampaignToEdit(campaign);
              setShowEditCampaignForm(true);
            }}
            onDelete={async () => {
              const confirmMessage = `Are you sure you want to delete \"${campaign.name}\"? This action cannot be undone.`;
              if (window.confirm(confirmMessage)) {
                try {
                  await apiService.deleteCampaign(campaign.id);
                  deleteCampaign(campaign.id);
                  if (club?.id) loadFinancialData();
                } catch (error) {
                  console.error('Failed to delete campaign:', error);
                  alert('Failed to delete campaign. Please try again.');
                }
              }
            }}
            onView={() => navigate(`/campaigns/${campaign.id}`)}
          />
        ))}
      </div>
    ) : (
      <div className="text-center py-12">
        <Target className="mx-auto h-12 w-12 text-gray-400" />
        <h3 className="mt-2 text-sm font-medium text-gray-900">No campaigns yet</h3>
        <p className="mt-1 text-sm text-gray-500">Create your first campaign to get started</p>
        <button
          onClick={() => setShowCreateCampaignForm(true)}
          className="mt-4 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg"
        >
          Create Campaign
        </button>
      </div>
    )}
  </div>
)}

{/* Create Campaign Form Modal */}
{showCreateCampaignForm && (
  <CreateCampaignForm
    onSubmit={async (campaignData) => {
      try {
        const response = await apiService.createCampaign(campaignData);
        addCampaign(response.campaign);
        if (club?.id) loadFinancialData();
        setShowCreateCampaignForm(false);
      } catch (error) {
        console.error('Failed to create campaign:', error);
      }
    }}
    onCancel={() => setShowCreateCampaignForm(false)}
  />
)}

{/* Edit Campaign Form Modal */}
{showEditCampaignForm && campaignToEdit && (
  <CreateCampaignForm
    onSubmit={async (campaignData) => {
      try {
        const response = await apiService.updateCampaign(campaignToEdit.id, campaignData);
        updateCampaign(campaignToEdit.id, response.campaign);
        if (club?.id) loadFinancialData();
        setShowEditCampaignForm(false);
        setCampaignToEdit(null);
      } catch (error) {
        console.error('Failed to update campaign:', error);
      }
    }}
    onCancel={() => {
