// client/src/App.tsx
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import  ClubRegistrationForm  from './components/auth/ClubRegistrationForm';
import  ClubDashboard  from './components/dashboard/ClubDashboard';
import CreateEventForm  from './components/events/CreateEventForm';
import  CreateCampaignForm  from './components/campaigns/CreateCampaignForm';
import EventExpenseManager  from './components/events/EventExpenseManager';

// Simple navigation component
const Navigation = () => {
  const location = useLocation();

  const navItems = [
    { path: '/', label: '🏠 Home', exact: true },
    { path: '/register', label: '📝 Club Registration' },
    { path: '/dashboard', label: '📊 Dashboard' },
    { path: '/create-event', label: '📅 Create Event' },
    { path: '/create-campaign', label: '🎯 Create Campaign' },
    { path: '/expense-manager', label: '💰 Expense Manager' },
  ];

  return (
    <nav style={{
      backgroundColor: '#1e40af',
      padding: '1rem',
      marginBottom: '2rem'
    }}>
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        display: 'flex',
        gap: '1rem',
        flexWrap: 'wrap'
      }}>
        {navItems.map(item => (
          <Link
            key={item.path}
            to={item.path}
            style={{
              color: location.pathname === item.path ? '#fbbf24' : 'white',
              textDecoration: 'none',
              padding: '0.5rem 1rem',
              borderRadius: '4px',
              backgroundColor: location.pathname === item.path ? '#1e3a8a' : 'transparent',
              fontSize: '0.875rem',
              fontWeight: location.pathname === item.path ? 'bold' : 'normal'
            }}
          >
            {item.label}
          </Link>
        ))}
      </div>
    </nav>
  );
};

// Home page showing available components
const HomePage = () => (
  <div style={{
    minHeight: '80vh',
    backgroundColor: '#f9fafb',
    padding: '2rem'
  }}>
    <div style={{
      maxWidth: '1200px',
      margin: '0 auto'
    }}>
      <h1 style={{
        fontSize: '3rem',
        fontWeight: 'bold',
        textAlign: 'center',
        marginBottom: '2rem',
        color: '#1e40af'
      }}>
        🎯 FundRaisely Club
      </h1>

      <div style={{
        backgroundColor: 'white',
        padding: '2rem',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        marginBottom: '2rem'
      }}>
        <h2 style={{
          fontSize: '1.5rem',
          fontWeight: 'bold',
          marginBottom: '1rem',
          color: '#374151'
        }}>
          ✅ Available Components (Click navigation above)
        </h2>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '1rem',
          marginTop: '1rem'
        }}>
          <div style={{
            backgroundColor: '#f0f9ff',
            border: '1px solid #0ea5e9',
            padding: '1rem',
            borderRadius: '4px'
          }}>
            <h3 style={{ color: '#0c4a6e', margin: '0 0 0.5rem 0' }}>📝 Club Registration</h3>
            <p style={{ color: '#374151', fontSize: '0.875rem', margin: 0 }}>
              Complete registration form with validation
            </p>
          </div>

          <div style={{
            backgroundColor: '#f0fdf4',
            border: '1px solid #22c55e',
            padding: '1rem',
            borderRadius: '4px'
          }}>
            <h3 style={{ color: '#14532d', margin: '0 0 0.5rem 0' }}>📊 Dashboard</h3>
            <p style={{ color: '#374151', fontSize: '0.875rem', margin: 0 }}>
              Full dashboard with metrics, events, campaigns
            </p>
          </div>

          <div style={{
            backgroundColor: '#fef3c7',
            border: '1px solid #f59e0b',
            padding: '1rem',
            borderRadius: '4px'
          }}>
            <h3 style={{ color: '#92400e', margin: '0 0 0.5rem 0' }}>📅 Create Event</h3>
            <p style={{ color: '#374151', fontSize: '0.875rem', margin: 0 }}>
              Event creation form with flexible types
            </p>
          </div>

          <div style={{
            backgroundColor: '#fdf2f8',
            border: '1px solid #ec4899',
            padding: '1rem',
            borderRadius: '4px'
          }}>
            <h3 style={{ color: '#831843', margin: '0 0 0.5rem 0' }}>🎯 Create Campaign</h3>
            <p style={{ color: '#374151', fontSize: '0.875rem', margin: 0 }}>
              Campaign setup with target amounts
            </p>
          </div>

          <div style={{
            backgroundColor: '#f5f3ff',
            border: '1px solid #8b5cf6',
            padding: '1rem',
            borderRadius: '4px'
          }}>
            <h3 style={{ color: '#581c87', margin: '0 0 0.5rem 0' }}>💰 Expense Manager</h3>
            <p style={{ color: '#374151', fontSize: '0.875rem', margin: 0 }}>
              Track expenses with categories and totals
            </p>
          </div>
        </div>
      </div>

      <div style={{
        backgroundColor: '#dcfce7',
        border: '1px solid #16a34a',
        color: '#15803d',
        padding: '1rem',
        borderRadius: '4px',
