import React, { useState } from 'react';
import { 
  Calendar, 
  DollarSign, 
  Users, 
  TrendingUp, 
  Plus, 
  Target,
  CheckCircle,
  Clock,
  Award,
  PieChart,
  TrendingDown,
  Receipt,
  X
} from 'lucide-react';

// Type definitions
interface MetricCardProps {
  icon: React.ComponentType<React.SVGProps<SVGSVGElement>>;
  title: string;
  value: string | number;
  subtitle?: string;
  color?: string;
}

interface Event {
  id: string;
  title: string;
  type: string;
  event_date: string;
  goal_amount: number;
  actual_amount: number;
  total_expenses: number;
  net_profit: number;
  status: 'live' | 'draft' | 'completed';
}

interface EventCardProps {
  event: Event;
}

interface Campaign {
  id: string;
  name: string;
  target_amount: number;
  current_amount: number;
  progress: number;
}

interface CampaignCardProps {
  campaign: Campaign;
}

interface CreateEventFormProps {
  onSubmit: (eventData: any) => void;
  onCancel: () => void;
  campaigns?: Campaign[];
}

interface CreateCampaignFormProps {
  onSubmit: (campaignData: any) => void;
  onCancel: () => void;
}

// Mock data for demonstration
const mockMetrics = {
  total_raised: 12450,
  total_expenses: 3200,
  net_profit: 9250,
  active_campaigns: 3,
  upcoming_events: 2,
  total_supporters: 156,
  pending_expenses: 450,
  top_donors: [
    { id: '1', name: 'Sarah Johnson', type: 'donor' as const, total: 500 },
    { id: '2', name: 'Mike Chen', type: 'sponsor' as const, total: 300 },
    { id: '3', name: 'Emma Wilson', type: 'donor' as const, total: 250 }
  ]
};

const mockUpcomingEvents: Event[] = [
  { 
    id: '1', 
    title: 'Christmas Quiz Night', 
    type: 'Quiz Night', 
    event_date: '2025-12-15',
    goal_amount: 2000,
