import React, { useState } from 'react';
import { Target, DollarSign, FileText, Save, X } from 'lucide-react';

interface CreateCampaignFormProps {
  onSubmit: (campaignData: any) => void; // Required - always provided by parent
  onCancel: () => void;
}

export default function CreateCampaignForm({ onSubmit, onCancel }: CreateCampaignFormProps) {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    target_amount: ''
  });

  const [errors, setErrors] = useState<Record<string, string>>({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Campaign name is required';
    }

    if (!formData.description.trim()) {
      newErrors.description = 'Campaign description is required';
    }

    if (!formData.target_amount || parseFloat(formData.target_amount) <= 0) {
      newErrors.target_amount = 'Target amount must be greater than 0';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      console.log('üì§ Submitting campaign data to parent handler');

      // Clean up form data before submitting (match backend expectations)
      const cleanedData = {
        name: formData.name.trim(),
        description: formData.description.trim(),
        target_amount: formData.target_amount // Parent will convert to number
      };

      console.log('üîÑ Cleaned campaign data:', cleanedData);

      // Call parent's submit handler
      await onSubmit(cleanedData);

      console.log('‚úÖ Campaign submitted successfully, clearing form');

      // Clear form after successful submission
      setFormData({
        name: '',
        description: '',
        target_amount: ''
      });

      // Clear any errors
      setErrors({});

      console.log('üßπ Campaign form cleared');

    } catch (error) {
      console.error('‚ùå Campaign form submission failed:', error);
      // Don't show alert here - let parent handle error display
      // Don't clear form on error - let user fix and retry
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-gray-500 bg-opacity-75 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg shadow-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="px-6 py-4 border-b border-gray-200 bg-green-50">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg mr-3">
                <Target className="h-6 w-6 text-green-600" />
              </div>
              <div>
                <h2 className="text-lg font-semibold text-gray-900">Create New Campaign</h2>
                <p className="text-sm text-gray-600">Set up a fundraising goal for your club</p>
              </div>
            </div>
            <button
              onClick={onCancel}
              className="text-gray-400 hover:text-gray-600"
              disabled={isSubmitting}
            >
              <X className="h-6 w-6" />
            </button>
          </div>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} className="px-6 py-4 space-y-6">
          {/* Campaign Name */}
          <div>
            <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">
              Campaign Name *
            </label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleInputChange}
              disabled={isSubmitting}
              className={`w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 ${
                errors.name ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-green-500'
              } ${isSubmitting ? 'bg-gray-50' : ''}`}
              placeholder="e.g., Community Center Renovation"
            />
            {errors.name && <p className="mt-1 text-sm text-red-600">{errors.name}</p>}
          </div>

          {/* Target Amount */}
          <div>
            <label htmlFor="target_amount" className="block text-sm font-medium text-gray-700 mb-1">
              Target Amount *
            </label>
            <div className="relative">
              <DollarSign className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
              <input
                type="number"
                id="target_amount"
                name="target_amount"
                value={formData.target_amount}
                onChange={handleInputChange}
                disabled={isSubmitting}
                className={`w-full pl-10 pr-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 ${
                  errors.target_amount ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-green-500'
                } ${isSubmitting ? 'bg-gray-50' : ''}`}
                placeholder="10000.00"
                min="0"
                step="0.01"
              />
            </div>
            {errors.target_amount && <p className="mt-1 text-sm text-red-600">{errors.target_amount}</p>}
          </div>

          {/* Description */}
          <div>
            <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
              Description *
            </label>
            <div className="relative">
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                disabled={isSubmitting}
                rows={4}
                className={`w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 ${
                  errors.description ? 'border-red-300 focus:border-red-500' : 'border-gray-300 focus:border-green-500'
                } ${isSubmitting ? 'bg-gray-50' : ''}`}
                placeholder="Describe what this campaign aims to achieve and why it's important to your community..."
              />
              <FileText className="absolute right-3 top-3 h-5 w-5 text-gray-400" />
            </div>
            {errors.description && <p className="mt-1 text-sm text-red-600">{errors.description}</p>}
            <p className="mt-1 text-sm text-gray-500">
              Provide details about your goals, how funds will be used, and expected impact.
            </p>
          </div>

          {/* Campaign Tips */}
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
            <h4 className="text-sm font-medium text-blue-800 mb-2">üí° Campaign Tips</h4>
            <ul className="text-sm text-blue-700 space-y-1">
              <li>‚Ä¢ Set realistic but ambitious goals based on your community size</li>
              <li>‚Ä¢ Include specific details about how funds will be used</li>
              <li>‚Ä¢ Plan multiple events to reach your target over time</li>
              <li>‚Ä¢ Consider breaking large goals into smaller milestones</li>
            </ul>
          </div>

          {/* Form Actions */}
          <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200">
            <button
              type="button"
              onClick={onCancel}
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-gray-700 bg-white border border-gray-300 rounded-md hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-green-500 disabled:opacity-50"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-4 py-2 text-sm font-medium text-white bg-green-600 border border-transparent rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 flex items-center disabled:opacity-50"
            >
              <Save className="h-4 w-4 mr-2" />
              {isSubmitting ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Creating...
                </>
              ) : (
                'Create Campaign'
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
